#include "Polynom.h"
#include <iostream>

int main()
{
    Polynom f("");
    Polynom s("");
    Polynom add, mul;
    add = f + s;
    mul = f * s;

    std::cout << "First pol: " << f.toBitString() << std::endl;
    std::cout << "Second pol: " << s.toBitString() << std::endl;
    std::cout << "ADDResult: " << add.toBitString() << std::endl;
    std::cout << "MULResult: " << mul.toBitString() << std::endl;
}


